--
-- Name: unacknowledged_required_policies; Type: VIEW; Schema: public; Owner: neondb_owner
--

CREATE VIEW public.unacknowledged_required_policies AS
 SELECT po.id AS portal_id,
    po.name AS portal_name,
    u.id AS user_id,
    p.id AS policy_id,
    p.title,
    p.organization_id,
    u.display_name AS user_name,
    u.email AS user_email,
        CASE
            WHEN ((po.acknowledgment_due_days IS NOT NULL) AND (p.published_at IS NOT NULL)) THEN ((p.published_at + ((po.acknowledgment_due_days || ' days'::text))::interval))::date
            ELSE NULL::date
        END AS due_date,
        CASE
            WHEN ((po.acknowledgment_due_days IS NOT NULL) AND (po.acknowledgment_reminder_days IS NOT NULL) AND (p.published_at IS NOT NULL)) THEN ((p.published_at + (((po.acknowledgment_due_days - po.acknowledgment_reminder_days) || ' days'::text))::interval))::date
            ELSE NULL::date
        END AS reminder_date,
        CASE
            WHEN ((po.acknowledgment_due_days IS NOT NULL) AND (p.published_at IS NOT NULL) AND ((p.published_at + ((po.acknowledgment_due_days || ' days'::text))::interval) < CURRENT_TIMESTAMP)) THEN 'overdue'::text
            WHEN ((po.acknowledgment_due_days IS NOT NULL) AND (p.published_at IS NOT NULL) AND ((p.published_at + ((po.acknowledgment_due_days || ' days'::text))::interval) <= (CURRENT_TIMESTAMP + '7 days'::interval))) THEN 'due_soon'::text
            WHEN (po.acknowledgment_due_days IS NOT NULL) THEN 'pending'::text
            ELSE 'no_deadline'::text
        END AS urgency_status
   FROM (((public.policies p
     JOIN public.policy_portal_assignments ppa ON ((p.id = ppa.policy_id)))
     JOIN public.portals po ON ((ppa.portal_id = po.id)))
     CROSS JOIN public.users u)
  WHERE ((po.requires_acknowledgment = true) AND (po.is_active = true) AND ((p.status)::text = 'published'::text) AND (u.organization_id = p.organization_id) AND (u.is_active = true) AND (((po.access_type)::text = 'public'::text) OR ((po.access_type)::text = 'authenticated'::text) OR (((po.access_type)::text = 'role_based'::text) AND ((u.role)::text = ANY (po.allowed_roles))) OR ((po.access_type)::text = 'password'::text)) AND (NOT (EXISTS ( SELECT 1
           FROM public.policy_acknowledgments pac
          WHERE ((pac.policy_id = p.id) AND (pac.user_id = u.id) AND (pac.organization_id = p.organization_id))))));


